import { __decorate } from "tslib";
let WordSearchComponent = class WordSearchComponent {
  constructor() {
    this.words = ['Angular', 'JavaScript', 'HTML', 'CSS'];
    this.grid = [['A', 'N', 'G', 'U', 'L', 'A', 'R', 'I', 'W', 'E', 'B'], ['J', 'A', 'V', 'A', 'S', 'C', 'R', 'I', 'P', 'T', 'C'], ['H', 'T', 'M', 'L', 'A', 'R', 'A', 'S', 'E', 'E', 'R'], ['S', 'S', 'S', 'S', 'S', 'S', 'S', 'S', 'S', 'S', 'S'], ['C', 'E', 'L', 'T', 'I', 'C', 'S', 'S', 'S', 'S', 'S'], ['S', 'S', 'S', 'S', 'S', 'S', 'S', 'S', 'S', 'S', 'S']];
    this.selectedLetters = [];
    this.selectedWord = null;
  }

  isLetterSelected(letter) {
    return this.selectedLetters.includes(letter);
  }

  isWordFound(word) {
    const flattenedGrid = this.grid.flat().join('');
    return flattenedGrid.includes(word);
  }

  onMouseDown(event) {
    this.selectedLetters = [];
    this.selectedWord = null;
    const cell = event.target;
    cell.classList.add('selected');
    const letter = cell.textContent;
    this.selectedLetters.push(letter);
  }

  onMouseUp() {
    const selectedWord = this.selectedLetters.join('');
    this.selectedLetters = [];
    const foundWord = this.words.find(word => word === selectedWord);

    if (foundWord) {
      alert(`Found word: ${foundWord}`);
    } else {
      alert(`Sorry, the selected word "${selectedWord}" was not found.`);
    }
  }

};
WordSearchComponent = __decorate([Component({
  selector: 'app-word-search',
  template: `
<div class ="outer">
  <table (mousedown)="onMouseDown($event)" (mouseup)="onMouseUp()">
    <tbody>
      <tr *ngFor="let row of grid">
        <td
          *ngFor="let letter of row"
          [ngClass]="{ 'selected': isLetterSelected(letter), 'found': isWordFound(word) }"
        >
          {{ letter }}
        </td>
      </tr>
    </tbody>
  </table>

  <p>Find the following words:</p>
  <ul>
    <li
      *ngFor="let word of words"
      [ngClass]="{ 'found': isWordFound(word) }"
    >
      {{ word }}
    </li>
  </ul>
</div>
 `,
  styles: [`
  .outer {
    padding: 20px;
    min-height: 100vh;
    background-color: lavender;
  }
  table {
    border-collapse: collapse;
  }

  td {
    width: 30px;
    height: 30px;
    border: 1px solid black;
    text-align: center;
    font-size: 20px;
    font-weight: bold;
  }

  .selected {
    background-color: lightblue;
  }

  .found {
    background-color: yellow;
  }

  li {
    display: inline-block;
    margin-right: 10px;
    font-weight: bold;
  }
  `]
})], WordSearchComponent);
export { WordSearchComponent };
