export class CardComponent {
  constructor() {
    this.cards = [];
    this.colors = ['#F44336', '#E91E63', '#9C27B0', '#673AB7', '#3F51B5', '#2196F3', '#03A9F4', '#00BCD4', '#009688', '#4CAF50', '#8BC34A', '#CDDC39', '#FFC107', '#FF9800', '#FF5722', '#795548', '#9E9E9E', '#607D8B', '#333333', '#FFFFFF'];
    const cardTitles = ['Card 1', 'Card 2', 'Card 3', 'Card 4', 'Card 5'];
    const cardContents = ['Lorem ipsum dolor sit amet, consectetur adipiscing elit.', 'Fusce at massa at velit efficitur pulvinar.', 'Praesent mattis, ante sed ullamcorper efficitur, lorem erat tristique nisi.', 'Maecenas ac est vel erat bibendum tincidunt a eu lorem.', 'Suspendisse non lobortis velit, at bibendum lorem.']; // Generate random pairs of colors from the colors array

    const pairs = this.generatePairs(this.colors); // Assign each pair of colors to two different cards

    for (let i = 1; i <= 20; i++) {
      const titleIndex = Math.floor(Math.random() * cardTitles.length);
      const contentIndex = Math.floor(Math.random() * cardContents.length);
      const title = cardTitles[titleIndex];
      const content = cardContents[contentIndex];
      const colorIndex = Math.floor(Math.random() * pairs.length);
      const [color1, color2] = pairs.splice(colorIndex, 1)[0];
      this.cards.push({
        id: i,
        title,
        content,
        color: color1,
        isFlipped: false
      }, {
        id: i + 20,
        title,
        content,
        color: color2,
        isFlipped: false
      });
    }
  }

  flipCard(card) {
    card.isFlipped = !card.isFlipped;
  }

  generatePairs(array) {
    let currentIndex = array.length;
    let temporaryValue;
    let randomIndex; // While there remain elements to shuffle...

    while (0 !== currentIndex) {
      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex--; // And swap it with the current element.

      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    } // Generate pairs of colors


    const pairs = [];

    for (let i = 0; i < array.length; i += 2) {
      pairs.push([array[i], array[i + 1]]);
    }

    return pairs;
  }

